import React from 'react'
import { Link } from "react-router-dom";

class Navbar extends React.Component {
    constructor(props) {
        super(props);
        this.state = { path: "" };
    }
    componentDidMount() {
        this.setState({ path: this.props.history.location.pathname }) 
    }
    render() {
        var path = this.state.path;
        if (path == "/") {
            var homeLink = (
                <li className="navItem homeLink"><Link to="/">//</Link></li>
            )
        } else if (path == "/works") {
            var homeLink = (
                <li className="navItem homeLink"><Link to="/works">WORKSBITCH</Link></li>
            )
        }
        return (
            <div className="navbarContainer">
                <ul className="navbar">
                    <li className="navItem"><Link to="/works">Works</Link></li>
                    <li className="navItem"><Link to="/about">About</Link></li>
                    <li className="navItem"><a href="#">Resume</a></li>
                </ul>
            </div>
        );
    }
}

export default Navbar;
