import React from 'react';
import ImageGallery from 'react-image-gallery';

// Images

import oldOne from '../images/owl-shop/old-one.png';
import oldTwo from '../images/owl-shop/old-two.png';
import gameOne from '../images/owl-shop/game-one.png';
import gameTwo from '../images/owl-shop/game-two.png';

import lofiOne from '../images/owl-shop/lofi-one.png';
import lofiTwo from '../images/owl-shop/lofi-two.png';
import lofiThree from '../images/owl-shop/lofi-three.png';
import lofiFour from '../images/owl-shop/lofi-four.png';

import lofiFive from '../images/owl-shop/lofi-five.png';
import finalVersion from '../images/owl-shop/final.png';


import galleryImg from '../images/owl-shop/gallery.png';

class StudyTwo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    componentDidMount() {
        this.refs.container.addEventListener('scroll', this.handleScroll.bind(this));
    }
    componentWillUnmount() {
        this.refs.container.removeEventListener('scroll', this.handleScroll.bind(this));
    }
    handleScroll(event) {
        let scrollTop = event.srcElement.body.scrollTop,
            itemTranslate = Math.min(0, scrollTop/3 - 60);
        let yOffset = window.pageYOffset;
        // if yOffset is > the div on the right's position then call this.props... and send upwards what the stage is into app.js
        // and then pass that stage into leftContainer via state in app.js...


        this.setState({
          transform: itemTranslate
        });
    }
    paneDidMount(node) {
        if (node) {
          node.addEventListener('scroll', () => console.log('scroll!'));
        }
      };
    render() {
        const images = [
            {
                original: oldOne
            },
            {
                original: oldTwo
            },
        ]
        const imagesTwo = [
            {
                original: gameOne
            },
            {
                original: gameTwo
            },
        ]
        const imagesThree = [
            {
                original: lofiThree
            },
            {
                original: lofiFour
            },
        ]
        if (this.props.currentView == "preview") {
            var startStudyLink = (
                <a onClick={ this.props.startStudy }>START THE STUDY!</a>
            )
            var className = "mobileHide"
        }
        var studyContent = (
            <div className="workOuterContainer">
                <div className="webHide startLinkContainer">
                    { startStudyLink }
                </div>
            </div>
        )
        return (
            <div>{ studyContent }</div>
        );
    }
}

export default StudyTwo
